import React, { useState } from 'react';
import { FaCreditCard, FaMoneyBillWave, FaQrcode, FaCalculator } from 'react-icons/fa';

const PaymentModal = ({ order, onClose, onComplete }) => {
  const [selectedMethod, setSelectedMethod] = useState(null);
  const [cashAmount, setCashAmount] = useState('');
  const [showCalculator, setShowCalculator] = useState(false);

  const paymentMethods = [
    { id: 'pix', name: 'PIX', icon: FaQrcode, color: '#32CD32' },
    { id: 'cash', name: 'Dinheiro', icon: FaMoneyBillWave, color: '#FFD700' },
    { id: 'debit', name: 'Débito', icon: FaCreditCard, color: '#4169E1' },
    { id: 'credit', name: 'Crédito', icon: FaCreditCard, color: '#FF4500' }
  ];

  const handleMethodSelect = (method) => {
    setSelectedMethod(method);
    if (method.id === 'cash') {
      setShowCalculator(true);
    } else {
      setShowCalculator(false);
    }
  };

  const handleCompletePayment = () => {
    let paymentData = {
      method: selectedMethod.id,
      amount: order.totalPrice
    };

    if (selectedMethod.id === 'cash' && cashAmount) {
      const received = parseFloat(cashAmount);
      const change = received - order.totalPrice;
      paymentData = {
        ...paymentData,
        received: received,
        change: change
      };
    }

    onComplete(order.id, paymentData);
  };

  const calculateChange = () => {
    if (!cashAmount) return 0;
    const received = parseFloat(cashAmount);
    return received - order.totalPrice;
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000
    }}>
      <div style={{
        background: '#fff',
        borderRadius: 12,
        padding: 24,
        maxWidth: 500,
        width: '90%',
        maxHeight: '90vh',
        overflow: 'auto'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 20 }}>
          <h2 style={{ margin: 0, color: '#333' }}>Finalizar Pedido</h2>
          <button 
            onClick={onClose}
            style={{
              background: 'none',
              border: 'none',
              fontSize: 24,
              cursor: 'pointer',
              color: '#666'
            }}
          >
            ×
          </button>
        </div>

        {/* Informações do pedido */}
        <div style={{ 
          background: '#f5f5f5', 
          padding: 16, 
          borderRadius: 8, 
          marginBottom: 20 
        }}>
          <div style={{ fontWeight: 600, marginBottom: 8 }}>
            Placa: {order.vehicle.plate}
          </div>
          <div style={{ marginBottom: 4 }}>
            Cliente: {order.vehicle.customer.name}
          </div>
          <div style={{ marginBottom: 4 }}>
            Serviços: {order.services.map(s => s.service.name).join(', ')}
          </div>
          <div style={{ fontWeight: 600, fontSize: 18, color: '#1976d2' }}>
            Total: R$ {order.totalPrice.toFixed(2)}
          </div>
        </div>

        {/* Métodos de pagamento */}
        <div style={{ marginBottom: 20 }}>
          <h3 style={{ marginBottom: 16, color: '#333' }}>Escolha o método de pagamento:</h3>
          <div style={{ 
            display: 'grid', 
            gridTemplateColumns: 'repeat(2, 1fr)', 
            gap: 12 
          }}>
            {paymentMethods.map((method) => (
              <button
                key={method.id}
                onClick={() => handleMethodSelect(method)}
                style={{
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  padding: '20px 16px',
                  border: selectedMethod?.id === method.id ? `2px solid ${method.color}` : '2px solid #e0e0e0',
                  borderRadius: 8,
                  background: selectedMethod?.id === method.id ? `${method.color}15` : '#fff',
                  cursor: 'pointer',
                  transition: 'all 0.2s ease',
                  minHeight: 100
                }}
                onMouseEnter={(e) => {
                  if (selectedMethod?.id !== method.id) {
                    e.target.style.borderColor = method.color;
                    e.target.style.background = `${method.color}10`;
                  }
                }}
                onMouseLeave={(e) => {
                  if (selectedMethod?.id !== method.id) {
                    e.target.style.borderColor = '#e0e0e0';
                    e.target.style.background = '#fff';
                  }
                }}
              >
                <method.icon 
                  size={32} 
                  color={method.color} 
                  style={{ marginBottom: 8 }}
                />
                <span style={{ 
                  fontWeight: 600, 
                  color: '#333',
                  fontSize: 14
                }}>
                  {method.name}
                </span>
              </button>
            ))}
          </div>
        </div>

        {/* Calculadora para dinheiro */}
        {showCalculator && (
          <div style={{ 
            background: '#f9f9f9', 
            padding: 16, 
            borderRadius: 8, 
            marginBottom: 20 
          }}>
            <div style={{ display: 'flex', alignItems: 'center', marginBottom: 12 }}>
              <FaCalculator style={{ marginRight: 8, color: '#666' }} />
              <span style={{ fontWeight: 600 }}>Valor recebido:</span>
            </div>
            <input
              type="number"
              value={cashAmount}
              onChange={(e) => setCashAmount(e.target.value)}
              placeholder="0,00"
              style={{
                width: '100%',
                padding: '12px',
                border: '1px solid #ddd',
                borderRadius: 6,
                fontSize: 16,
                marginBottom: 8
              }}
            />
            {cashAmount && (
              <div style={{ 
                display: 'flex', 
                justifyContent: 'space-between',
                padding: '8px 0',
                borderTop: '1px solid #ddd'
              }}>
                <span>Troco:</span>
                <span style={{ 
                  fontWeight: 600, 
                  color: calculateChange() >= 0 ? '#388e3c' : '#f44336'
                }}>
                  R$ {calculateChange().toFixed(2)}
                </span>
              </div>
            )}
          </div>
        )}

        {/* Botões de ação */}
        <div style={{ display: 'flex', gap: 12 }}>
          <button
            onClick={onClose}
            style={{
              flex: 1,
              padding: '12px',
              border: '1px solid #ddd',
              borderRadius: 6,
              background: '#fff',
              cursor: 'pointer',
              fontWeight: 600
            }}
          >
            Cancelar
          </button>
          <button
            onClick={handleCompletePayment}
            disabled={!selectedMethod || (selectedMethod.id === 'cash' && !cashAmount)}
            style={{
              flex: 1,
              padding: '12px',
              border: 'none',
              borderRadius: 6,
              background: selectedMethod ? '#388e3c' : '#ccc',
              color: '#fff',
              cursor: selectedMethod ? 'pointer' : 'not-allowed',
              fontWeight: 600
            }}
          >
            Finalizar
          </button>
        </div>
      </div>
    </div>
  );
};

export default PaymentModal;
