import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { getDashboardOrders, updateOrderStatus } from '../../services/api';
import WorkOrderCard from './WorkOrderCard';
import './Kanban.css';
import axios from 'axios';

const KanbanBoard = () => {
    const [columns, setColumns] = useState({
        AWAITING: { name: 'Aguardando', items: [] },
        WASHING: { name: 'Em Lavagem', items: [] },
        READY: { name: 'Pronto p/ Retirada', items: [] },
    });
    const [employees, setEmployees] = useState([]);
    const [selectedEmployee, setSelectedEmployee] = useState('');

    const columnColors = {
        AWAITING: '#e3f2fd', // azul claro
        WASHING: '#fff3e0', // laranja claro
        READY: '#e8f5e9',   // verde claro
    };

    useEffect(() => {
        const fetchOrders = async () => {
            const { data: orders } = await getDashboardOrders();
            const newColumns = {
                AWAITING: { name: 'Aguardando', items: [] },
                WASHING: { name: 'Em Lavagem', items: [] },
                READY: { name: 'Pronto p/ Retirada', items: [] },
            };
            orders.forEach(order => {
                if (selectedEmployee && order.employee && order.employee.id !== selectedEmployee) return;
                if (newColumns[order.status]) {
                    newColumns[order.status].items.push(order);
                }
            });
            setColumns(newColumns);
        };
        const fetchEmployees = async () => {
            const { data } = await axios.get('/api/employees');
            setEmployees(data);
        };
        fetchOrders();
        fetchEmployees();
    }, [selectedEmployee]);

    const onDragEnd = async (result) => {
        const { source, destination } = result;
        if (!destination) return;

        const sourceColumn = columns[source.droppableId];
        const destColumn = columns[destination.droppableId];
        const sourceItems = [...sourceColumn.items];
        const [removed] = sourceItems.splice(source.index, 1);

        if (source.droppableId === destination.droppableId) {
            sourceItems.splice(destination.index, 0, removed);
            setColumns({
                ...columns,
                [source.droppableId]: { ...sourceColumn, items: sourceItems },
            });
        } else {
            const destItems = [...destColumn.items];
            destItems.splice(destination.index, 0, removed);
            setColumns({
                ...columns,
                [source.droppableId]: { ...sourceColumn, items: sourceItems },
                [destination.droppableId]: { ...destColumn, items: destItems },
            });
            // Atualiza o status no backend
            await updateOrderStatus(removed.id, destination.droppableId);
        }
    };

    return (
        <div className="kanban-board" style={{ padding: 24 }}>
            {/* Indicadores rápidos */}
            <div style={{ display: 'flex', gap: 24, marginBottom: 16, alignItems: 'center' }}>
                <div style={{ background: '#1976d2', color: '#fff', borderRadius: 8, boxShadow: '0 2px 8px #0001', padding: 16, minWidth: 120, textAlign: 'center' }}>
                    <div style={{ fontSize: 16, fontWeight: 600 }}>Aguardando</div>
                    <div style={{ fontSize: 22, fontWeight: 700 }}>{columns.AWAITING.items.length}</div>
                </div>
                <div style={{ background: '#ffa726', color: '#fff', borderRadius: 8, boxShadow: '0 2px 8px #0001', padding: 16, minWidth: 120, textAlign: 'center' }}>
                    <div style={{ fontSize: 16, fontWeight: 600 }}>Em Lavagem</div>
                    <div style={{ fontSize: 22, fontWeight: 700 }}>{columns.WASHING.items.length}</div>
                </div>
                <div style={{ background: '#388e3c', color: '#fff', borderRadius: 8, boxShadow: '0 2px 8px #0001', padding: 16, minWidth: 120, textAlign: 'center' }}>
                    <div style={{ fontSize: 16, fontWeight: 600 }}>Pronto</div>
                    <div style={{ fontSize: 22, fontWeight: 700 }}>{columns.READY.items.length}</div>
                </div>
                <div style={{ flex: 1 }} />
                {/* Filtro por funcionário */}
                <div>
                    <select value={selectedEmployee} onChange={e => setSelectedEmployee(e.target.value)} style={{ padding: 8, borderRadius: 6, border: '1px solid #ccc', minWidth: 180, background: '#f9f9f9' }}>
                        <option value="">Todos os funcionários</option>
                        {employees.map(emp => (
                            <option key={emp.id} value={emp.id}>{emp.name}</option>
                        ))}
                    </select>
                </div>
            </div>
            {/* Kanban */}
            <div style={{ display: 'flex', gap: 24, alignItems: 'flex-start' }}>
                {Object.entries(columns).map(([columnId, column]) => (
                    <div key={columnId} className="kanban-column" style={{ background: columnColors[columnId], borderRadius: 12, padding: 16, minWidth: 320, flex: 1, boxShadow: '0 2px 8px #0001' }}>
                        <h3 style={{ textAlign: 'center', marginBottom: 16 }}>{column.name}</h3>
                        <Droppable droppableId={columnId}>
                            {(provided) => (
                                <div ref={provided.innerRef} {...provided.droppableProps} style={{ minHeight: 120 }}>
                                    {column.items.map((item, index) => (
                                        <Draggable key={item.id} draggableId={item.id} index={index}>
                                            {(provided, snapshot) => (
                                                <div ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps} style={{ marginBottom: 16, opacity: snapshot.isDragging ? 0.7 : 1, ...provided.draggableProps.style }}>
                                                    <WorkOrderCard order={item} />
                                                </div>
                                            )}
                                        </Draggable>
                                    ))}
                                    {provided.placeholder}
                                </div>
                            )}
                        </Droppable>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default KanbanBoard;
