import React, { useEffect, useState } from 'react';
import axios from 'axios';

const EmployeePage = () => {
  const [employees, setEmployees] = useState([]);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [orders, setOrders] = useState([]);

  const statusMap = {
    AWAITING: 'Aguardando',
    WASHING: 'Em lavagem',
    READY: 'Pronto',
    FINISHED: 'Finalizado',
  };

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      const { data } = await axios.get('/api/employees');
      setEmployees(data);
    } catch (err) {
      setEmployees([]);
    }
  };

  const fetchOrders = async (employeeId) => {
    try {
      const { data } = await axios.get(`/api/orders?employeeId=${employeeId}`);
      setOrders(data);
    } catch (err) {
      setOrders([]);
    }
  };

  const handleSelectEmployee = (e) => {
    const empId = e.target.value;
    setSelectedEmployee(empId);
    if (empId) fetchOrders(empId);
    else setOrders([]);
  };

  return (
    <div className="employee-page-container">
      <h2>Funcionários</h2>
      <label>Selecione um funcionário:</label>
      <select value={selectedEmployee || ''} onChange={handleSelectEmployee} style={{ marginBottom: 24 }}>
        <option value="">-- Selecione --</option>
        {employees.map(emp => (
          <option key={emp.id} value={emp.id}>{emp.name}</option>
        ))}
      </select>
      {selectedEmployee && (
        <table className="services-table">
          <thead>
            <tr>
              <th>Veículo</th>
              <th>Valor do Serviço</th>
              <th>Ganho Funcionário</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(order => (
              <tr key={order.id}>
                <td>{order.vehicle?.model} ({order.vehicle?.plate})</td>
                <td>R$ {order.totalPrice.toFixed(2)}</td>
                <td>
                  {order.employee ? `R$ ${(order.totalPrice * (order.employee.percentage / 100)).toFixed(2)} (${order.employee.percentage}%)` : '-'}
                </td>
                <td>{statusMap[order.status] || order.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default EmployeePage;
