import React from 'react';
import { FaUserCircle } from 'react-icons/fa';

function timeAgo(dateString) {
  const now = new Date();
  const date = new Date(dateString);
  const diffMs = now - date;
  const diffMin = Math.floor(diffMs / 60000);
  if (diffMin < 1) return 'agora';
  if (diffMin < 60) return `há ${diffMin}min`;
  const diffH = Math.floor(diffMin / 60);
  if (diffH < 24) return `há ${diffH}h`;
  const diffD = Math.floor(diffH / 24);
  return `há ${diffD}d`;
}

const WorkOrderCard = ({ order, onFinish }) => {
  const employee = order.employee;
  const customer = order.vehicle?.customer;
  return (
    <div className="workorder-card" style={{ border: 'none', borderRadius: 12, padding: 16, marginBottom: 8, background: '#fff', boxShadow: '0 2px 8px #0002', position: 'relative' }}>
      <div style={{ fontWeight: 600, fontSize: 16, marginBottom: 2 }}>Placa: {order.vehicle.plate}</div>
      <div style={{ color: '#1976d2', fontWeight: 500, marginBottom: 2 }}>Cliente: {customer?.name}</div>
      <div style={{ marginBottom: 2 }}><b>Serviços:</b> <span title={order.services.map(s => s.service.name).join(', ')}>{order.services.map(s => s.service.name).join(', ')}</span></div>
      <div style={{ marginBottom: 2 }}><b>Total:</b> R$ {order.totalPrice.toFixed(2)}</div>
      {employee && (
        <div style={{ display: 'flex', alignItems: 'center', gap: 8, marginBottom: 2 }}>
          <span title={`Funcionário: ${employee.name}\nGanho: ${employee.percentage}%\nCliente: ${customer?.name}\nTelefone: ${customer?.phone}`}
                style={{ display: 'flex', alignItems: 'center', gap: 4, cursor: 'pointer' }}>
            <span style={{
              width: 28, height: 28, borderRadius: '50%', background: '#1976d2', color: '#fff', display: 'flex', alignItems: 'center', justifyContent: 'center', fontWeight: 700, fontSize: 16
            }}>{employee.name[0]}</span>
            <span style={{ fontWeight: 500 }}>{employee.name}</span>
            <span style={{ color: '#888', fontSize: 13 }}>({employee.percentage}%)</span>
          </span>
        </div>
      )}
      <div style={{ color: '#888', fontSize: 13, marginTop: 4 }}>Criado {timeAgo(order.createdAt)}</div>
      {order.status !== 'READY' && onFinish && (
        <button onClick={() => onFinish(order.id)} style={{ marginTop: 10, background: '#388e3c', color: '#fff', border: 'none', borderRadius: 6, padding: '6px 14px', cursor: 'pointer', fontWeight: 600 }}>
          Finalizar
        </button>
      )}
    </div>
  );
};

export default WorkOrderCard;
