import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { getDashboardOrders, updateOrderStatus } from '../../services/api';
import WorkOrderCard from './WorkOrderCard';
import './Kanban.css';
import axios from 'axios';

const KanbanBoard = () => {
    const [columns, setColumns] = useState({
        AWAITING: { name: 'Aguardando', items: [] },
        WASHING: { name: 'Em Lavagem', items: [] },
        READY: { name: 'Pronto p/ Retirada', items: [] },
    });
    const [employees, setEmployees] = useState([]);
    const [selectedEmployee, setSelectedEmployee] = useState('');

    const columnColors = {
        AWAITING: '#e3f2fd', // azul claro
        WASHING: '#fff3e0', // laranja claro
        READY: '#e8f5e9',   // verde claro
    };

    useEffect(() => {
        const fetchOrders = async () => {
            const { data: orders } = await getDashboardOrders();
            const newColumns = {
                AWAITING: { name: 'Aguardando', items: [] },
                WASHING: { name: 'Em Lavagem', items: [] },
                READY: { name: 'Pronto p/ Retirada', items: [] },
            };
            orders.forEach(order => {
                if (selectedEmployee && order.employee && order.employee.id !== selectedEmployee) return;
                if (newColumns[order.status]) {
                    newColumns[order.status].items.push(order);
                }
            });
            setColumns(newColumns);
        };
        const fetchEmployees = async () => {
            const { data } = await axios.get('/api/employees');
            setEmployees(data);
        };
        fetchOrders();
        fetchEmployees();
    }, [selectedEmployee]);

    const onDragEnd = async (result) => {
        const { source, destination } = result;
        if (!destination) return;

        const sourceColumn = columns[source.droppableId];
        const destColumn = columns[destination.droppableId];
        const sourceItems = [...sourceColumn.items];
        const [removed] = sourceItems.splice(source.index, 1);

        if (source.droppableId === destination.droppableId) {
            sourceItems.splice(destination.index, 0, removed);
            setColumns({
                ...columns,
                [source.droppableId]: { ...sourceColumn, items: sourceItems },
            });
        } else {
            const destItems = [...destColumn.items];
            destItems.splice(destination.index, 0, removed);
            setColumns({
                ...columns,
                [source.droppableId]: { ...sourceColumn, items: sourceItems },
                [destination.droppableId]: { ...destColumn, items: destItems },
            });
            // Atualiza o status no backend
            await updateOrderStatus(removed.id, destination.droppableId);
        }
    };

    const handleFinish = async (orderId) => {
        await updateOrderStatus(orderId, 'READY');
        // Atualiza as colunas após finalizar
        const { data: orders } = await getDashboardOrders();
        const newColumns = {
            AWAITING: { name: 'Aguardando', items: [] },
            WASHING: { name: 'Em Lavagem', items: [] },
            READY: { name: 'Pronto p/ Retirada', items: [] },
        };
        orders.forEach(order => {
            if (newColumns[order.status]) {
                newColumns[order.status].items.push(order);
            }
        });
        setColumns(newColumns);
    };

    return (
        <div className="kanban-board" style={{ width: '100%' }}>
            {/* Indicadores rápidos em bloco separado */}
            <div style={{ display: 'flex', justifyContent: 'center', gap: 32, margin: '0 auto 24px auto', maxWidth: 600, width: '100%' }}>
                <div style={{ background: '#fff', borderRadius: 8, padding: '10px 22px', minWidth: 100, textAlign: 'center', boxShadow: '0 1px 4px #0001', border: '1px solid #eee' }}>
                    <div style={{ fontSize: 14, fontWeight: 600, color: '#222' }}>Aguardando</div>
                    <div style={{ fontSize: 22, color: '#1976d2', fontWeight: 700 }}>{columns.AWAITING.items.length}</div>
                </div>
                <div style={{ background: '#fff', borderRadius: 8, padding: '10px 22px', minWidth: 100, textAlign: 'center', boxShadow: '0 1px 4px #0001', border: '1px solid #eee' }}>
                    <div style={{ fontSize: 14, fontWeight: 600, color: '#222' }}>Em Lavagem</div>
                    <div style={{ fontSize: 22, color: '#ffa726', fontWeight: 700 }}>{columns.WASHING.items.length}</div>
                </div>
                <div style={{ background: '#fff', borderRadius: 8, padding: '10px 22px', minWidth: 100, textAlign: 'center', boxShadow: '0 1px 4px #0001', border: '1px solid #eee' }}>
                    <div style={{ fontSize: 14, fontWeight: 600, color: '#222' }}>Pronto</div>
                    <div style={{ fontSize: 22, color: '#388e3c', fontWeight: 700 }}>{columns.READY.items.length}</div>
                </div>
            </div>
            {/* Kanban colunas lado a lado */}
            <div style={{ display: 'flex', gap: 24, alignItems: 'flex-start', width: '100%' }}>
                {Object.values(columns).every(col => col.items.length === 0) ? (
                    <div style={{ textAlign: 'center', width: '100%' }}>
                        <p>Nenhuma ordem de serviço encontrada.</p>
                    </div>
                ) : (
                    Object.entries(columns).map(([columnId, column]) => (
                        <Droppable key={columnId} droppableId={columnId}>
                            {(provided) => (
                                <div className="kanban-column" ref={provided.innerRef} {...provided.droppableProps} style={{ flex: 1 }}>
                                    <h3>{column.name}</h3>
                                    {column.items.map((item, index) => (
                                        <Draggable key={item.id} draggableId={item.id} index={index}>
                                            {(provided) => (
                                                <div ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                                                    <WorkOrderCard order={item} onFinish={handleFinish} />
                                                </div>
                                            )}
                                        </Draggable>
                                    ))}
                                    {provided.placeholder}
                                </div>
                            )}
                        </Droppable>
                    ))
                )}
            </div>
        </div>
    );
};

export default KanbanBoard;
